CREATE TABLE IF NOT EXISTS `manulife`.`InvitationList` ( `id` VARCHAR(6) NOT NULL , `name` VARCHAR(250) NOT NULL , `grade` VARCHAR(250) NOT NULL , `client` VARCHAR(250) NOT NULL , `project` VARCHAR(250) NOT NULL , `manager` VARCHAR(250) NOT NULL , `office` VARCHAR(250) NOT NULL , `department` VARCHAR(250) NOT NULL , PRIMARY KEY (`id`));